import './App.css'
import { Button, Tabs, Tab } from '@material-ui/core'
import { useState } from 'react'
import SortBox from './SortBox'

function App() {
    const [algSel, setAlgSel] = useState(0);
    const [numElements, setNumElements] = useState(30);
    const [array, setArray] = useState(genArray(numElements));
    const [comparisons, setComparisons] = useState(0);
    const [sorting, setSorting] = useState(false);

    const handleChange = (e, v) => {
        setAlgSel(v);
    };

    const sort = () => {
        if(!sorting){
            setSorting(true)
            let choice;
            switch(algSel){
                case 0:
                    choice = bubbleSort;
                case 1:
                    choice = selectionSort;
                case 2:
                    choice = insertionSort;
                case 4:
                    choice = mergeSort;
                case 5:
                    choice = QuickSort;
                default:
                    choice = bubbleSort;
            }
            const alg = choice(array);
            const algInt = setInterval(() => {
            const next = alg.next();
            if(next.done) {
                setSorting(false);
                clearInterval(algInt);
            }
            else {
                setComparisons(c=>c+1);
                setArray([...next.value]);
            }
        },5)
        }
    }

    return (
        <>
            <div className="header">
                <h1>Sorting Algorithm Visualizer</h1>
            </div>
            <Tabs
                    value={algSel}
                    onChange={handleChange}
                    indicatorColor="primary"
                    textColor="primary"
                    variant="scrollable"
                    scrollButtons="auto">
                    <Tab label="Bubble Sort" />
                    <Tab label="Selection Sort" />
                    <Tab label="Insertion Sort" />
                    <Tab label="Merge Sort" />
                    <Tab label="QuickSort" />
                </Tabs>
            <SortBox 
                sort={sort}
                array={array} 
                setArray={setArray} 
                genArray={genArray} 
                comparisons={comparisons}
                setComparisons={setComparisons}
                numElements={numElements} 
                setNumElements={setNumElements} />
        </>
    )
}

function genArray(n) {
    let ret = [];
    for(let i = 0; i < n; i++) {
        ret[i] = i*(150/30);
    }
    return(shuffle(ret));
}

function shuffle(array) {
    var currentIndex = array.length,  randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      // And swap it with the current element.
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array;
  }

  function* bubbleSort(arr)
  {
    let i, j;
    let array = [...arr];
    for (i = 0; i < array.length-1; i++)
    {
        for (j = 0; j < array.length-i-1; j++)
        {
            if (array[j] > array[j+1])
            {
                let temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;
            }
            yield array;
        }
    }
}

function* insertionSort(a) {
    let inputArr = [...a];
    let n = inputArr.length;
        for (let i = 1; i < n; i++) {
            // Choosing the first element in our unsorted subarray
            let current = inputArr[i];
            // The last element of our sorted subarray
            let j = i-1; 
            while ((j > -1) && (current < inputArr[j])) {
                inputArr[j+1] = inputArr[j];
                yield inputArr;
                j--;
            }
            inputArr[j+1] = current;
        }
    yield inputArr;
}

function* selectionSort(a)
{
    let arr = [...a];
    let i, j, min_idx;

    // One by one move boundary of unsorted subarray
    for (i = 0; i < arr.length-1; i++)
    {
        // Find the minimum element in unsorted array
        min_idx = i;
        for (j = i + 1; j < arr.length; j++){
            if (arr[j] < arr[min_idx]) {
                min_idx = j;
            }
            yield arr;
        }

        // Swap the found minimum element with the first element
        let temp = arr[min_idx];
        arr[min_idx] = arr[i];
        arr[i] = temp;
    }
    yield arr;
}



function* mergeSort(arr) {
    //Create two arrays for sorting
    let sorted = Array.from(arr);
    let n = sorted.length;
    let buffer = new Array(n);

    for (let size = 1; size < n; size *= 2) {
      for (let leftStart = 0; leftStart < n; leftStart += 2*size) {

        //Get the two sub arrays
        let left = leftStart,
            right = Math.min(left + size, n),
            leftLimit = right,
            rightLimit = Math.min(right + size, n);

        //Merge the sub arrays
        let i = left;
        while (left < leftLimit && right < rightLimit) {
            if (sorted[left] <= sorted[right]) {
              buffer[i++] = sorted[left++];
            } else {
              buffer[i++] = sorted[right++];
            }
            yield [...buffer, ...sorted.slice(buffer.length)]
          }

          //If there are elements in the left sub arrray then add it to the result
          while (left < leftLimit) {
            buffer[i++] = sorted[left++];
            yield [...buffer, ...sorted.slice(buffer.length)]
          }

          //If there are elements in the right sub array then add it to the result
          while (right < rightLimit) {
            buffer[i++] = sorted[right++];
            yield [...buffer, ...sorted.slice(buffer.length)]

          }
      }

      //Swap the sorted sub array and merge them
      let temp = sorted;
      sorted = buffer;
      buffer = temp;
    }

    yield sorted;
  }

  function* QuickSort(a){
    let arr = [...a];
    //Stack for storing start and end index
    let stack = [];

    //Get the start and end index
    let start = 0;
    let end = arr.length - 1;

    //Push start and end index in the stack
    stack.push({x: start, y: end});

    //Iterate the stack
    while(stack.length){
      //Get the start and end from the stack
      const { x, y } = stack.shift();

      //Partition the array along the pivot
      let pivot = arr[y];
        let i = x;

        //Partition the array into two parts using the pivot
        for(let j = x; j < y; j++){
            if(arr[j] <= pivot){      
                const temp = arr[i]
                arr[i] = arr[j]
                arr[j] = temp;
            i++;
            }
            yield arr;
        }
        const temp = arr[i]
        arr[i] = arr[y]
        arr[y] = temp;
      const PI = i;

      //Push sub array with less elements than pivot into the stack
      if(PI - 1 > x){
        stack.push({x: x, y: PI - 1});
      }

      //Push sub array with greater elements than pivot into the stack
      if(PI + 1 < y){
        stack.push({x: PI + 1, y: y});
      }
    }
  }

