import Element from './Element'
import ElementCont from './ElementCont'
import { Container, Button } from '@material-ui/core'
import { useState } from 'react'

function App() {
    const [list, setList] = useState(genArray(30));

    function swap(i,j) {
        let newArr = list;
        const temp = list[i];
        newArr[i] = list[j];
        newArr[j] = temp;
        setList(newArr);

    }

    return (
        <Container>
            <ElementCont title="Bubble Sort">
                {
                    list.map((x,i)=>{
                        return(<Element key={i} height={x}></Element>)
                    })
                }
            </ElementCont>
            <Button title="Test" onClick={()=>{swap(0,1)}}>Test</Button>
        </Container>
    )
}

function genArray(n) {
    let ret = [];
    for(let i = 0; i < n; i++) {
        ret[i] = i*(150/30);
    }
    return(shuffle(ret));
}

function shuffle(array) {
    var currentIndex = array.length,  randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      // And swap it with the current element.
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array;
  }

export default App;
