import Element from './Element'
import ElementCont from './ElementCont'
import { Container, Button } from '@material-ui/core'
import { useState } from 'react'

function App() {
    const [list, setList] = useState(genArray(30));
    const [comparisons, setComparisons] = useState(0);
    const [selected, setSelected] = useState([]);

    function swap(i,j) {
        let newArr = list;
        const temp = list[i];
        newArr[i] = list[j];
        newArr[j] = temp;
        setList(newArr);
        setComparisons(comparisons+1);
    }

    function bubbleSort(t) {
        let i = 0;
        let j = 0;
        function step() {
            if (i < list.length) {
                if(j < list.length - i - 1) {
                    setSelected([j,j+1]);
                    setTimeout(() =>{
                        if(list[j] > list[j+1]) {
                            swap(j,j+1);
                        }
                        j++;
                    },t/2);
                }
                else {
                    j = 0;
                    i++;
                }
                return(true);
            }
            return(false);

        }
        let inter = setInterval(()=>{
            console.log("Interval");
            if(!step()) {
                clearInterval(inter);
                setSelected([]);
            }
        },t)
    }

    return (
        <Container>
            <ElementCont comparisons={comparisons} title="Bubble Sort">
                {
                    list.map((x,i)=>{
                        return(<Element color={(i === selected[0] || i === selected[1]) ? "#48A89E" : undefined} key={i} height={x}></Element>)
                    })
                }
            </ElementCont>
            <Button title="Test" onClick={()=>{bubbleSort(50)}}>Test</Button>
        </Container>
    )
}

function genArray(n) {
    let ret = [];
    for(let i = 0; i < n; i++) {
        ret[i] = i*(150/30);
    }
    return(shuffle(ret));
}

function shuffle(array) {
    var currentIndex = array.length,  randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      // And swap it with the current element.
      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array;
  }

export default App;
